mapper.update:
update tbl_tester
set name=#{name},
    phone=#{phone}
where id = #{id}

mapper.select:
select xxx.*, tbl_tester.name, tbl_tester.phone, tbl_tester.create_time from (select temp_count.tester_id, sum( case when ((type = 1 and answer = 'A') or (type = 2 and answer = 'D')) then result_count end ) as red_count, sum( case when ((type = 1 and answer = 'B') or (type = 2 and answer = 'C')) then result_count end ) as blue_count, sum( case when ((type = 1 and answer = 'C') or (type = 2 and answer = 'B')) then result_count end ) as yellow_count, sum( case when ((type = 1 and answer = 'D') or (type = 2 and answer = 'A')) then result_count end ) as green_count from (select tq.type, ttr.tester_id, ttr.answer, count(ttr.id) result_count from tbl_test_result ttr left join tbl_question tq on ttr.question_id = tq.id
group by tq.type, ttr.tester_id, ttr.answer) temp_count group by temp_count.tester_id) xxx left join tbl_tester on xxx.tester_id = tbl_tester.id
tester_id = #{testerId}

select count( case when ((ifnull(red_count,0)>=ifnull(yellow_count,0)) and (ifnull(red_count,0)>=ifnull(blue_count,0)) and (ifnull(red_count,0)>=ifnull(green_count,0))) then red_count when (red_count=yellow_count=15 or red_count=blue_count=15 or red_count=green_count=15) then red_count end) redCount, count( case when ((ifnull(yellow_count,0) >ifnull(red_count,0)) and (ifnull(yellow_count,0) >= ifnull(blue_count,0)) and (ifnull(yellow_count,0) >= ifnull(green_count,0))) then yellow_count when (yellow_count=blue_count=15 or yellow_count=green_count=15) then yellow_count end ) yellowCount, count( case when ((ifnull(blue_count,0)>ifnull(red_count,0)) and (ifnull(blue_count,0)>ifnull(yellow_count,0)) and (ifnull(blue_count,0)>=ifnull(green_count,0))) then blue_count when (blue_count=green_count=15) then blue_count end ) blueCount, count( case when ((ifnull(green_count,0)>ifnull(red_count,0)) and (ifnull(green_count,0)>ifnull(blue_count,0)) and (ifnull(green_count,0)>ifnull(yellow_count,0))) then green_count end ) greenCount
from (select * from (select temp_count.tester_id, sum( case when ((type = 1 and answer = 'A') or (type = 2 and answer = 'D')) then ifnull(result_count,0) end ) as red_count, sum( case when ((type = 1 and answer = 'B') or (type = 2 and answer = 'C')) then ifnull(result_count,0) end ) as blue_count, sum( case when ((type = 1 and answer = 'C') or (type = 2 and answer = 'B')) then ifnull(result_count,0) end ) as yellow_count, sum( case when ((type = 1 and answer = 'D') or (type = 2 and answer = 'A')) then ifnull(result_count,0) end ) as green_count from (select tq.type, ttr.tester_id, ttr.answer, count(ttr.id) result_count from tbl_test_result ttr left join tbl_question tq on ttr.question_id = tq.id
group by tq.type, ttr.tester_id, ttr.answer) temp_count group by temp_count.tester_id) xxx inner join tbl_tester on xxx.tester_id = tbl_tester.id) tester;
